    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
       
        int pso = Integer.parseInt(jTextField1.getText());
        int tlla = Integer.parseInt(jTextField2.getText());
        int edd = Integer.parseInt(jTextField3.getText());
        
        h.setPeso(pso);
        h.setTalla(tlla);
        h.setEdad(edd);
    }//GEN-LAST:event_jButton2ActionPerformed

